<script lang="ts">
    // const thePath = document.querySelector('path')
    // const pathLength = document.querySelector('path').getTotalLength();
    // console.log(pathLength);

    import { onMount } from 'svelte';

    let hue = 270; // 色相の開始点
    let lastUpdate = Date.now();
    const updateInterval = 100; // 色相を更新する間隔（ミリ秒）

    const generateColor = (hue) => `hsl(${hue}, 100%, 50%)`;

    const updateHue = () => {
        const now = Date.now();
        if (now - lastUpdate > updateInterval) { // 前回の更新から一定時間経過したら更新
            hue = (hue + 1) % 360; // ここでの色相の変化量や範囲を調整する
            lastUpdate = now;
        }

        requestAnimationFrame(updateHue); // 次のフレームでも更新を続ける
    };

    onMount(() => {
        updateHue(); // アニメーション開始
    });
</script>

